mapper.select:
SELECT k.id AS id, k.name AS name, k.category_id AS categoryId, k.difficulty_level AS difficultyLevel, k.cover_image AS coverImage, k.view_count AS viewCount, k.is_published, k.description, k.steps, k.created_at, c.name AS categoryName FROM knots k INNER JOIN knot_categories c ON k.category_id = c.id
AND k.name LIKE CONCAT('%', #{query.keyword, jdbcType=VARCHAR}, '%')
AND k.category_id = #{query.categoryId, jdbcType=BIGINT}
AND k.id = #{query.knotId, jdbcType=BIGINT}
AND k.is_published = #{query.isPublished, jdbcType=BOOLEAN}
